package dynamicprogramming.shortestcommonsupersequence;

public class Solution {
    static int COMMON = 1;
    static int CHOSE_FIRST = 2;
    static int CHOSE_SECOND = 3;
    private int[][] cache;
    private int[][] chose;

    public String shortestCommonSupersequence(String str1, String str2) {
        if (str1 == null || str1.isEmpty()) {
            return str2;
        }
        if (str2 == null || str2.isEmpty()) {
            return str1;
        }
        cache = new int[str1.length()][];
        chose = new int[str1.length()][];
        for (int i = 0; i < str1.length(); i++) {
            cache[i] = new int[str2.length()];
            chose[i] = new int[str2.length()];
        }
        int shortestLength = scs(str1, 0, str2, 0);
        int row = 0;
        int col = 0;
        StringBuilder sb = new StringBuilder();
        while (true) {
            if (row >= str1.length() && col >= str2.length()) {
                return sb.toString();
            }
            if (row >= str1.length()) {
                sb.append(str2.charAt(col));
                col ++;
                continue;
            }
            if (col >= str2.length()) {
                sb.append(str1.charAt(row));
                row ++;
                continue;
            }
            if (chose[row][col] == CHOSE_FIRST) {
                sb.append(str1.charAt(row));
                row ++;
            } else if (chose[row][col] == CHOSE_SECOND) {
                sb.append(str2.charAt(col));
                col ++;
            } else {
                sb.append(str1.charAt(row));
                row ++;
                col ++;
            }
        }
    }

    private int scs(String str1, int i, String str2, int j) {
        if (i == str1.length() && j == str2.length()) {
            return 0;
        }
        if (i == str1.length()) {
            return str2.length() - j;
        }
        if (j == str2.length()) {
            return str1.length() - i;
        }
        if (cache[i][j] != 0) {
            return cache[i][j];
        }
        if (str1.charAt(i) == str2.charAt(j)) {
            int result = 1 + scs(str1, i + 1, str2, j + 1);
            chose[i][j] = COMMON;
            cache[i][j] = result;
            return result;
        }
        int first = 1 + scs(str1, i + 1, str2, j);
        int second = 1 + scs(str1, i, str2, j + 1);
        int result;
        if (first < second) {
            result = first;
            chose[i][j] = CHOSE_FIRST;
        } else {
            result = second;
            chose[i][j] = CHOSE_SECOND;
        }
        cache[i][j] = result;
        return result;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.shortestCommonSupersequence("abac", "cab"));
        System.out.println(solution.shortestCommonSupersequence("abac", "ab"));
        System.out.println(solution.shortestCommonSupersequence("abac", ""));
        //
        System.out.println(solution.shortestCommonSupersequence("aaaaaaaa", "aaaaaaaaa"));
        System.out.println(solution.shortestCommonSupersequence("", ""));
        // ""
        //""
    }
}
